gauge &lt;- function(nr){
  comb &lt;- vector()

  n1 &lt;- round(nr*10000)%%10

  if(n1!=0){
    nr &lt;- nr-s1[n1]
    comb &lt;- c(comb, s1[n1])
    s1 &lt;- s1[-n1]
  }

  n2 &lt;- round(nr*1000)%%50
  if(n2!=0){
    nr &lt;- nr-s2[round(n2)]
    comb &lt;- c(comb, s2[n2])
    s2 &lt;- s2[-n2]
  }

  n3 &lt;- round((round(nr*100)%%100)/5)


  if(n3!=0 && n3&lt;20){
    nr &lt;- nr-s3[n3]
    comb&lt;-c(comb, s3[n3])
    s3 &lt;- s3[-n3]
  }

  if(nr&gt;=10 | 10-nr&lt;10^(-5)){
    comb &lt;- c(comb,s4)
    nr &lt;- nr-10
  } else {

    for (i in seq(1,4)){
      if(nr&gt;=s4[5-i] | s4[5-i]-nr&lt;10^(-5)){
        nr &lt;- nr-s4[5-i]
        comb &lt;- c(comb, s4[5-i])
      }
    }
  }

  if(round(nr, digits=5)==0){
    return(comb)
  }

  if(nr&gt;=sum(s3) | sum(s3)-nr&lt;10^(-5)){
    comb &lt;- c(comb,s3)
    nr &lt;- nr-sum(s3)
  } else {
    while(nr&gt;10^(-5) && length(s3)!=0){
      m3 &lt;- max(which(s3&lt;=nr+10^(-5)))
      nr &lt;- nr-s3[m3]
      if(nr&lt;s3[1] && nr&gt;10^(-5)){
        nr &lt;- nr+s3[m3]
        m3 &lt;- m3-1
        nr &lt;- nr-s3[m3]
      }
      comb &lt;- c(comb,s3[m3])
      s3 &lt;- s3[-m3]
    }
  }
  # if(round(nr, digits=5)==0){
  #   return(comb)
  # }
