  #   comb &lt;- comb[-which(comb==k)]
  #   while(nr&gt;10^(-5) && length(s2)!=0){
  #     m2 &lt;- max(which(s2&lt;=nr+10^(-5)))
  #     # print(m2)
  #     nr &lt;- nr-s2[m2]
  #     # print(nr)
  #     if(any(abs(comb-nr)&lt;10^(-5))){
  #       # print('d')
  #       nr &lt;- nr+s2[m2]
  #       m2 &lt;- m2-1
  #       nr &lt;- nr-s2[m2]
  #     }
  #     if(nr&lt;s2[1] && nr&gt;10^(-5)){
  #       nr &lt;- nr+s2[m2]
  #       m2 &lt;- min(which(s2&lt;=nr+10^(-5)))
  #       nr &lt;- nr-s2[m2]
  #     }
  #     # print(nr)
